import 'package:flutter/material.dart';class CustomText extends StatelessWidget {  String txt;  double size;  Color color;  FontWeight fontWeight;  CustomText(this.txt, this.size, this.color,      {this.fontWeight = FontWeight.w500});  @override  Widget build(BuildContext context) {    return Text(      txt,      style: TextStyle(        fontSize: size,        color: color,        fontFamily: 'NeoSans',        fontWeight: fontWeight,      ),    );  }}